import React, { Component } from 'react';
import Navigation from './Components/Navigation/Navigation.js';
import Showcase from './Components/Showcase/showcase.js';
import Tabs from './Components/Tabs/tabs.js';
import TabOneContent from './Components/Tabs/TabOneContent/TabOneContent.js';
import TabTwoContent from './Components/Tabs/TabTwoContent/TabTwoContent.js';
import TabThreeContent from './Components/Tabs/TabThreeContent/TabThreeContent.js';
import './App.css';

const initialState = {
	route: 'landing',
	isSignedIn: false,
	selectedTab: 'tab-1'
}

class App extends Component {

  constructor() {
  	super();
  	this.state = initialState;
  }

  handleTabChange = (selectedTab) => {
  	this.setState({selectedTab: selectedTab});
  }



  handleRouteChange = (route) => {
    if (route === 'landing') {
      this.setState(initialState)
    } else if (route === 'home') {
      this.setState({isSignedIn: true})
    }
    this.setState({route: route});
  }

  render() {
    return (
    <div className="App">
        	<Showcase />
       		<Tabs handleTabChange={this.handleTabChange}/>
          if (selectedTab === 'tab-1') {
            <div>
              <TabOneContent />
            </div>
          } else if (selectedTab === 'tab-2') {
            <div>
              <TabTwoContent />
            </div>
          } else if (selectedTab === 'tab-3') {
            <div>
              <TabThreeContent />
            </div>
          }

    </div>
    )
  };
}

export default App;
