import React, { Component, useRef } from 'react';
import Navigation from './Components/Navigation/Navigation.js';
import Showcase from './Components/Showcase/showcase.js';
import Modal from './Components/Modal/modal.js';
import Tabs from './Components/Tabs/tabs.js';
import TabOneContent from './Components/Tabs/TabOneContent/TabOneContent.js';
import TabTwoContent from './Components/Tabs/TabTwoContent/TabTwoContent.js';
import TabThreeContent from './Components/Tabs/TabThreeContent/TabThreeContent.js';
import './App.css';

const initialState = {
	route: 'landing',
	isModalOpen: false,
	selectedTab: 'tab-1'
}

class App extends Component {

  constructor() {
  	super();
  	this.state = initialState;
  }

  handleTabChange = (selectedTab) => {
  	this.setState({selectedTab: selectedTab});
  }

  removeBorder = () => {
    this.state.selectedTab.classList.add('tab-border');
  }

  onCallToAction = () => {
    this.setState({isModalOpen: true});
  }

  render() {
    return (
    <div className="App">
        	<Showcase onCallToAction={this.onCallToAction}/>
          <Modal isModalOpen={this.state.isModalOpen} />
       		<Tabs selectedTab={this.state.selectedTab} handleTabChange={this.handleTabChange} onCallToAction={this.state.isModalOpen} />
          {(() => {
            switch (this.state.selectedTab) {
              case 'tab-1':
                return <TabOneContent />;
              case 'tab-2':
                return <TabTwoContent />;
              case 'tab-3':
                return <TabThreeContent />;
              default:
                return null;
            }
          })()
        }
    </div>
    )
  };
}

export default App;
